import clsx from 'clsx';
import React from 'react';
import { connect } from 'react-redux';
import { useMeasure } from 'react-use';

import Box from '@material-ui/core/Box';
import { makeStyles } from '@material-ui/core/styles';

import LCDText from './LCDText';

const useStyles = makeStyles(
  () => ({
    root: {
      background: 'black',
      /*
      backgroundImage:
        'url(data:image/png;base64,);',
      backgroundColor: 'rgb(149, 177, 89)',
      */
      alignItems: 'center',
      display: 'flex',
      flexDirection: 'column',
      height: '100%',
      overflow: 'hidden',
      position: 'relative',
      userSelect: 'none'
    }
  }),
  { name: 'LCDClockPanel' }
);

/**
 * Panel that shows the status of a clock in the style of a classic
 * 7-segment LCD display.
 */
const LCDClockPanel = () => {
  const classes = useStyles();
  const [ref, { height }] = useMeasure();

  return (
    <Box className={clsx(classes.root)}>
      <Box display="flex" flexDirection="row">
        <LCDText offSegments color="red" decoration="glow" p={0.5}>
          MTC
        </LCDText>
        <LCDText offSegments off color="red" decoration="glow" p={0.5}>
          TIME
        </LCDText>
      </Box>
      <Box ref={ref} flex={1} overflow="hidden">
        <LCDText
          offSegments
          height={height}
          color="red"
          decoration="glow"
          variant="7segment"
        >
          00:17:41
        </LCDText>
      </Box>
    </Box>
  );
};

LCDClockPanel.propTypes = {};

LCDClockPanel.defaultProps = {};

export default connect(
  // mapStateToProps
  state => ({}),
  // mapDispatchToProps
  null
)(LCDClockPanel);
